import React from 'react';
import moment from 'moment';
import { FiUser, FiUsers, FiCheck, FiCheckCircle } from 'react-icons/fi';
import './ChatItem.css';

function ChatItem({ chat, isSelected, onClick, language }) {
  const getInitials = (name) => {
    if (!name) return 'U';
    return name.split(' ')
      .map(word => word[0])
      .join('')
      .substring(0, 2)
      .toUpperCase();
  };

  const formatTime = (timestamp) => {
    if (!timestamp) return '';
    
    const now = moment();
    const messageTime = moment(timestamp);
    
    if (now.diff(messageTime, 'days') === 0) {
      return messageTime.format(language === 'he' ? 'HH:mm' : 'h:mm A');
    } else if (now.diff(messageTime, 'days') < 7) {
      return messageTime.format('dddd');
    } else {
      return messageTime.format(language === 'he' ? 'DD/MM/YYYY' : 'MM/DD/YYYY');
    }
  };

  const truncateMessage = (message, maxLength = 50) => {
    if (!message) return '';
    if (message.length <= maxLength) return message;
    return message.substring(0, maxLength) + '...';
  };

  const isGroup = chat.isGroup || (typeof chat.id === 'string' && chat.id.includes('@g.us'));
  const chatName = chat.name || chat.pushname || (isGroup ? 'Group' : 'Unknown');
  const lastMessage = chat.lastMessage?.body || chat.lastMessage || '';
  const timestamp = chat.lastMessage?.timestamp || chat.timestamp;
  const unreadCount = chat.unreadCount || 0;
  const isOnline = chat.presence === 'available';

  return (
    <div 
      className={`chat-item ${isSelected ? 'selected' : ''} ${language === 'he' ? 'rtl' : ''}`}
      onClick={onClick}
    >
      <div className="chat-avatar">
        <div className="avatar">
          {chat.profilePic ? (
            <img src={chat.profilePic} alt={chatName} />
          ) : (
            <>
              {isGroup ? <FiUsers size={18} /> : <FiUser size={18} />}
              <span className="avatar-text">{getInitials(chatName)}</span>
            </>
          )}
        </div>
        {!isGroup && isOnline && (
          <div className="online-indicator" />
        )}
      </div>

      <div className="chat-content">
        <div className="chat-header">
          <h3 className="chat-name" title={chatName}>
            {chatName}
          </h3>
          <div className="chat-time">
            {formatTime(timestamp)}
          </div>
        </div>

        <div className="chat-preview">
          <div className="message-preview">
            {chat.lastMessage?.fromMe && (
              <span className="message-status">
                {chat.lastMessage?.ack === 3 ? (
                  <FiCheckCircle size={14} className="read-status" />
                ) : (
                  <FiCheck size={14} className="sent-status" />
                )}
              </span>
            )}
            <span className="message-text">
              {isGroup && chat.lastMessage?.author && !chat.lastMessage?.fromMe && (
                <span className="message-author">
                  {chat.lastMessage.author.split('@')[0]}: 
                </span>
              )}
              {lastMessage ? truncateMessage(lastMessage) : (
                <span className="no-message">
                  {language === 'he' ? ' 注转' : 'No messages'}
                </span>
              )}
            </span>
          </div>

          <div className="chat-badges">
            {chat.isMuted && (
              <div className="muted-badge" title={language === 'he' ? '砖转拽' : 'Muted'}>
                
              </div>
            )}
            {chat.isPinned && (
              <div className="pinned-badge" title={language === 'he' ? '注抓' : 'Pinned'}>
                
              </div>
            )}
            {unreadCount > 0 && (
              <div className="unread-badge">
                {unreadCount > 99 ? '99+' : unreadCount}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default ChatItem;
