import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Sidebar from './components/Sidebar';
import ChatWindow from './components/ChatWindow';
import QRCodeWindow from './components/QRCodeWindow';
import './App.css';

function App() {
  const [selectedChat, setSelectedChat] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('checking');
  const [language, setLanguage] = useState('en');

  // Check WhatsApp connection status
  useEffect(() => {
    let retryCount = 0;
    const maxRetries = 3;
    
    const checkConnection = async () => {
      try {
        const response = await axios.get('/whatsapp/status');
        setIsConnected(response.data.connected);
        setConnectionStatus(response.data.connected ? 'connected' : 'disconnected');
        retryCount = 0; // Reset retry count on success
      } catch (error) {
        console.error('Error checking connection:', error);
        
        // Handle rate limiting with exponential backoff
        if (error.response?.status === 429) {
          retryCount++;
          const backoffTime = Math.min(1000 * Math.pow(2, retryCount), 30000); // Max 30 seconds
          console.log(`Rate limited. Retrying in ${backoffTime}ms...`);
          setTimeout(checkConnection, backoffTime);
          return;
        }
        
        setIsConnected(false);
        setConnectionStatus('error');
      }
    };

    checkConnection();
    
    // Check connection status every 15 seconds (reduced frequency)
    const interval = setInterval(checkConnection, 15000);
    return () => clearInterval(interval);
  }, []);

  const handleChatSelect = (chat) => {
    setSelectedChat(chat);
  };

  const toggleLanguage = () => {
    setLanguage(prev => prev === 'en' ? 'he' : 'en');
  };

  if (!isConnected && connectionStatus !== 'checking') {
    return <QRCodeWindow language={language} toggleLanguage={toggleLanguage} />;
  }

  return (
    <div className={`app ${language === 'he' ? 'rtl' : ''}`}>
      <div className="app-container">
        <Sidebar 
          onChatSelect={handleChatSelect}
          selectedChat={selectedChat}
          language={language}
          toggleLanguage={toggleLanguage}
        />
        <ChatWindow 
          selectedChat={selectedChat}
          language={language}
        />
      </div>
    </div>
  );
}

export default App;
