import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { 
  FiSend, 
  FiPaperclip, 
  FiSmile, 
  FiPhone, 
  FiVideo,
  FiSearch,
  FiUser,
  FiUsers
} from 'react-icons/fi';
import { BsThreeDotsVertical } from 'react-icons/bs';
import MessageList from './MessageList';
import './ChatWindow.css';

const translations = {
  en: {
    typeMessage: 'Type a message',
    send: 'Send',
    selectChat: 'Select a chat to start messaging',
    loading: 'Loading messages...',
    error: 'Error loading messages',
    retry: 'Retry',
    online: 'online',
    lastSeen: 'last seen',
    typing: 'typing...',
    voiceCall: 'Voice call',
    videoCall: 'Video call',
    search: 'Search',
    more: 'More'
  },
  he: {
    typeMessage: 'הקלד הודעה',
    send: 'שלח',
    selectChat: 'בחר צ\'אט כדי להתחיל להודעה',
    loading: 'טוען הודעות...',
    error: 'שגיאה בטעינת הודעות',
    retry: 'נסה שוב',
    online: 'מחובר',
    lastSeen: 'נראה לאחרונה',
    typing: 'מקליד...',
    voiceCall: 'שיחת קול',
    videoCall: 'שיחת וידאו',
    search: 'חפש',
    more: 'עוד'
  }
};

function ChatWindow({ selectedChat, language }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [sending, setSending] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  const t = translations[language];

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    if (selectedChat) {
      fetchMessages();
      inputRef.current?.focus();
    }
  }, [selectedChat]);

  const fetchMessages = async () => {
    if (!selectedChat?.id) return;
    
    try {
      setLoading(true);
      setError(null);
      
      // Encode the chatId to handle special characters
      const encodedChatId = encodeURIComponent(String(selectedChat.id));
      console.log('Fetching messages for chatId:', selectedChat.id, 'encoded:', encodedChatId);
      const response = await axios.get(`/whatsapp/get-messages?chatId=${encodedChatId}&limit=50`);
      
      if (response.data.success && response.data.data?.messages) {
        setMessages(response.data.data.messages);
      } else if (response.data.messages) {
        // Some APIs might return messages directly
        setMessages(response.data.messages);
      } else {
        setError(response.data.error || 'Failed to load messages');
      }
    } catch (error) {
      console.error('Error fetching messages:', error);
      if (error.response?.status === 429) {
        setError('Too many requests. Please wait before loading messages.');
      } else if (error.response?.status === 400) {
        setError('Invalid chat ID or chat not found');
      } else {
        setError(error.response?.data?.error || 'Failed to load messages');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !selectedChat?.id || sending) return;

    const messageText = newMessage.trim();
    setNewMessage('');
    setSending(true);

    try {
      const payload = {
        chatId: String(selectedChat.id),
        message: messageText
      };
      
      console.log('Sending message with payload:', payload);
      const response = await axios.post('/whatsapp/send-message', payload);

      if (response.data.success || response.status === 200) {
        const newMsg = {
          id: Date.now().toString(),
          body: messageText,
          fromMe: true,
          timestamp: Date.now(),
          ack: 1
        };
        setMessages(prev => [...prev, newMsg]);
        setTimeout(fetchMessages, 1000);
      } else {
        setError(response.data.error || 'Failed to send message');
        setNewMessage(messageText);
      }
    } catch (error) {
      console.error('Error sending message:', error);
      if (error.response?.status === 400) {
        setError('Invalid message format or chat not found');
      } else {
        setError(error.response?.data?.error || 'Failed to send message');
      }
      setNewMessage(messageText);
    } finally {
      setSending(false);
    }
  };

  const handleInputChange = (e) => {
    setNewMessage(e.target.value);
    
    if (!isTyping && e.target.value.length > 0) {
      setIsTyping(true);
      setTimeout(() => setIsTyping(false), 3000);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage(e);
    }
  };

  const getChatName = () => {
    if (!selectedChat) return '';
    return selectedChat.name || selectedChat.pushname || 'Unknown';
  };

  const getChatStatus = () => {
    if (!selectedChat) return '';
    
    const isGroup = selectedChat.isGroup || (typeof selectedChat.id === 'string' && selectedChat.id.includes('@g.us'));
    if (isGroup) {
      return `${selectedChat.participants?.length || 0} participants`;
    }
    
    if (selectedChat.presence === 'available') {
      return t.online;
    }
    
    if (selectedChat.lastSeen) {
      return `${t.lastSeen} ${new Date(selectedChat.lastSeen).toLocaleString()}`;
    }
    
    return '';
  };

  if (!selectedChat) {
    return (
      <div className={`chat-window no-chat ${language === 'he' ? 'rtl' : ''}`}>
        <div className="no-chat-content">
          <div className="whatsapp-web-logo">
            <FiUsers size={120} className="logo-icon" />
          </div>
          <h2>WhatsApp Web</h2>
          <p>{t.selectChat}</p>
        </div>
      </div>
    );
  }

  const isGroup = selectedChat.isGroup || (typeof selectedChat.id === 'string' && selectedChat.id.includes('@g.us'));

  return (
    <div className={`chat-window ${language === 'he' ? 'rtl' : ''}`}>
      <div className="chat-header">
        <div className="chat-info">
          <div className="chat-avatar">
            <div className="avatar avatar-sm">
              {selectedChat.profilePic ? (
                <img src={selectedChat.profilePic} alt={getChatName()} />
              ) : (
                <>
                  {isGroup ? <FiUsers size={16} /> : <FiUser size={16} />}
                </>
              )}
            </div>
            {!isGroup && selectedChat.presence === 'available' && (
              <div className="online-indicator" />
            )}
          </div>
          <div className="chat-details">
            <h3 className="chat-name">{getChatName()}</h3>
            <p className="chat-status">
              {isTyping ? t.typing : getChatStatus()}
            </p>
          </div>
        </div>

        <div className="chat-actions">
          <button className="action-btn" title={t.search}>
            <FiSearch size={20} />
          </button>
          {!isGroup && (
            <>
              <button className="action-btn" title={t.voiceCall}>
                <FiPhone size={20} />
              </button>
              <button className="action-btn" title={t.videoCall}>
                <FiVideo size={20} />
              </button>
            </>
          )}
          <button className="action-btn" title={t.more}>
            <BsThreeDotsVertical size={20} />
          </button>
        </div>
      </div>

      <div className="messages-container">
        {loading ? (
          <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>{t.loading}</p>
          </div>
        ) : error ? (
          <div className="error-container">
            <p>{t.error}</p>
            <button onClick={() => fetchMessages()} className="btn btn-primary">
              {t.retry}
            </button>
          </div>
        ) : (
          <MessageList 
            messages={messages} 
            language={language}
            chatId={selectedChat.id}
          />
        )}
        <div ref={messagesEndRef} />
      </div>

      <div className="message-input-container">
        <form onSubmit={handleSendMessage} className="message-form">
          <button type="button" className="attachment-btn" title="Attach">
            <FiPaperclip size={20} />
          </button>
          
          <div className="input-wrapper">
            <input
              ref={inputRef}
              type="text"
              value={newMessage}
              onChange={handleInputChange}
              onKeyPress={handleKeyPress}
              placeholder={t.typeMessage}
              className="message-input"
              disabled={sending}
            />
            <button type="button" className="emoji-btn" title="Emoji">
              <FiSmile size={20} />
            </button>
          </div>

          <button 
            type="submit" 
            className={`send-btn ${newMessage.trim() ? 'active' : ''}`}
            disabled={!newMessage.trim() || sending}
            title={t.send}
          >
            {sending ? (
              <div className="loading-spinner small" />
            ) : (
              <FiSend size={20} />
            )}
          </button>
        </form>
      </div>
    </div>
  );
}

export default ChatWindow;
