import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FiSearch, FiMoreVertical, FiMessageCircle, FiSettings, FiGlobe } from 'react-icons/fi';
import { BsThreeDotsVertical } from 'react-icons/bs';
import ChatItem from './ChatItem';
import './Sidebar.css';

const translations = {
  en: {
    search: 'Search or start new chat',
    chats: 'Chats',
    newChat: 'New chat',
    settings: 'Settings',
    loading: 'Loading chats...',
    error: 'Error loading chats',
    noChats: 'No chats available',
    retry: 'Retry'
  },
  he: {
    search: 'חפש או התחל צ\'אט חדש',
    chats: 'צ\'אטים',
    newChat: 'צ\'אט חדש',
    settings: 'הגדרות',
    loading: 'טוען צ\'אטים...',
    error: 'שגיאה בטעינת צ\'אטים',
    noChats: 'אין צ\'אטים זמינים',
    retry: 'נסה שוב'
  }
};

function Sidebar({ onChatSelect, selectedChat, language, toggleLanguage }) {
  const [chats, setChats] = useState([]);
  const [filteredChats, setFilteredChats] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastRequestTime, setLastRequestTime] = useState(0);

  const t = translations[language];

  const fetchChats = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('Fetching chats...');
      const response = await axios.get('/whatsapp/get-chats');
      console.log('Chats response:', response.data);
      
      if (response.data.success && response.data.data?.chats) {
        setChats(response.data.data.chats);
        setFilteredChats(response.data.data.chats);
      } else if (response.data.chats) {
        // Some APIs might return chats directly
        setChats(response.data.chats);
        setFilteredChats(response.data.chats);
      } else if (Array.isArray(response.data)) {
        // Some APIs might return array directly
        setChats(response.data);
        setFilteredChats(response.data);
      } else {
        setError(response.data.error || 'Failed to load chats');
      }
    } catch (error) {
      console.error('Error fetching chats:', error);
      
      // Handle rate limiting specifically
      if (error.response?.status === 429) {
        setError('Too many requests. Please wait before refreshing chats.');
      } else {
        setError(error.response?.data?.error || 'Failed to load chats');
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchChats();
    
    // Refresh chats every 45 seconds (reduced frequency)
    const interval = setInterval(fetchChats, 45000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    if (searchTerm) {
      const filtered = chats.filter(chat =>
        chat.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        chat.lastMessage?.body?.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredChats(filtered);
    } else {
      setFilteredChats(chats);
    }
  }, [searchTerm, chats]);

  const handleChatClick = async (chat) => {
    // Simply select the chat without trying to click it in WhatsApp interface
    // The click-chat endpoint might not be implemented or working
    onChatSelect(chat);
  };

  const handleRetry = () => {
    fetchChats();
  };

  return (
    <div className={`sidebar ${language === 'he' ? 'rtl' : ''}`}>
      <div className="sidebar-header">
        <div className="sidebar-user">
          <div className="avatar">
            <FiMessageCircle size={20} />
          </div>
          <div className="user-info">
            <h3>WhatsApp</h3>
            <div className="status-indicator online"></div>
          </div>
        </div>
        
        <div className="sidebar-actions">
          <button 
            className="action-btn"
            onClick={toggleLanguage}
            title={language === 'en' ? 'Switch to Hebrew' : 'Switch to English'}
          >
            <FiGlobe size={20} />
          </button>
          <button className="action-btn" title={t.settings}>
            <FiSettings size={20} />
          </button>
          <button className="action-btn" title="More options">
            <BsThreeDotsVertical size={20} />
          </button>
        </div>
      </div>

      <div className="search-container">
        <div className="search-box">
          <FiSearch size={16} className="search-icon" />
          <input
            type="text"
            placeholder={t.search}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
        </div>
      </div>

      <div className="chats-section">
        <div className="chats-header">
          <h2>{t.chats}</h2>
          <button className="new-chat-btn" title={t.newChat}>
            <FiMessageCircle size={16} />
          </button>
        </div>

        <div className="chats-list">
          {loading ? (
            <div className="loading-container">
              <div className="loading-spinner"></div>
              <p>{t.loading}</p>
            </div>
          ) : error ? (
            <div className="error-container">
              <p>{t.error}</p>
              <button onClick={handleRetry} className="btn btn-primary">
                {t.retry}
              </button>
            </div>
          ) : filteredChats.length === 0 ? (
            <div className="no-chats">
              <FiMessageCircle size={48} className="no-chats-icon" />
              <p>{searchTerm ? 'No chats match your search' : t.noChats}</p>
            </div>
          ) : (
            filteredChats.map((chat, index) => (
              <ChatItem
                key={chat.id ? String(chat.id) : `chat-${index}`}
                chat={chat}
                isSelected={selectedChat?.id === chat.id}
                onClick={() => handleChatClick(chat)}
                language={language}
              />
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default Sidebar;
