import React, { useState, useEffect } from 'react';
import axios from 'axios';
import QRCode from 'react-qr-code';
import { FiSmartphone, FiRefreshCw, FiGlobe } from 'react-icons/fi';
import './QRCodeWindow.css';

const translations = {
  en: {
    title: 'Use WhatsApp on your computer',
    step1: '1. Open WhatsApp on your phone',
    step2: '2. Tap Menu',
    step3: '3. Tap Linked devices',
    step4: '4. Tap Link a device',
    step5: '5. Point your phone to this screen to capture the code',
    loading: 'Loading QR code...',
    refresh: 'Refresh QR code',
    connecting: 'Connecting...',
    error: 'Error loading QR code',
    keepPhoneConnected: 'Keep your phone connected to the internet'
  },
  he: {
    title: 'השתמש בוואטסאפ במחשב שלך',
    step1: '1. פתח את ווטסאפ בטלפון שלך',
    step2: '2. הקש על תפריט',
    step3: '3. הקש על מכשירים מקושרים',
    step4: '4. הקש על קישור מכשיר',
    step5: '5. כוון את הטלפון למסך זה כדי לצלם את הקוד',
    loading: 'טוען קוד QR...',
    refresh: 'רענן קוד QR',
    connecting: 'מתחבר...',
    error: 'שגיאה בטעינת קוד QR',
    keepPhoneConnected: 'שמור על הטלפון שלך מחובר לאינטרנט'
  }
};

function QRCodeWindow({ language, toggleLanguage }) {
  const [qrCode, setQrCode] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [connecting, setConnecting] = useState(false);
  const [lastRequestTime, setLastRequestTime] = useState(0);

  const t = translations[language];

  const fetchQRCode = async () => {
    const now = Date.now();
    // Prevent requests more frequent than every 10 seconds
    if (now - lastRequestTime < 10000) {
      console.log('Request throttled - too soon after last request');
      return;
    }
    
    try {
      setLoading(true);
      setError(null);
      setLastRequestTime(now);
      const response = await axios.get('/whatsapp/qr');
      setQrCode(response.data.qrCode);
    } catch (error) {
      console.error('Error fetching QR code:', error);
      
      // Handle rate limiting specifically
      if (error.response?.status === 429) {
        setError('Too many requests. Please wait a moment before refreshing.');
      } else {
        setError(error.response?.data?.error || 'Failed to load QR code');
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchQRCode();
    
    // Refresh QR code every 60 seconds (reduced frequency)
    const interval = setInterval(fetchQRCode, 60000);
    return () => clearInterval(interval);
  }, []);

  const handleRefresh = () => {
    fetchQRCode();
  };

  return (
    <div className={`qr-window ${language === 'he' ? 'rtl' : ''}`}>
      <div className="qr-container">
        <div className="qr-header">
          <div className="whatsapp-logo">
            <FiSmartphone size={40} color="#00a884" />
          </div>
          <h1 className="qr-title">{t.title}</h1>
          <button 
            className="language-toggle"
            onClick={toggleLanguage}
            title="Toggle Language"
          >
            <FiGlobe size={20} />
            {language === 'en' ? 'עב' : 'EN'}
          </button>
        </div>

        <div className="qr-content">
          <div className="qr-code-section">
            <div className="qr-code-container">
              {loading ? (
                <div className="qr-loading">
                  <div className="loading-spinner"></div>
                  <p>{t.loading}</p>
                </div>
              ) : error ? (
                <div className="qr-error">
                  <p>{t.error}</p>
                  <p className="error-message">{error}</p>
                  <button onClick={handleRefresh} className="btn btn-primary">
                    <FiRefreshCw size={16} />
                    {t.refresh}
                  </button>
                </div>
              ) : qrCode ? (
                <div className="qr-display">
                  <QRCode 
                    value={qrCode} 
                    size={280}
                    bgColor="#ffffff"
                    fgColor="#000000"
                    level="M"
                  />
                  <button onClick={handleRefresh} className="refresh-btn">
                    <FiRefreshCw size={16} />
                  </button>
                </div>
              ) : (
                <div className="qr-empty">
                  <p>No QR code available</p>
                </div>
              )}
            </div>

            {connecting && (
              <div className="connecting-status">
                <div className="loading-spinner"></div>
                <p>{t.connecting}</p>
              </div>
            )}
          </div>

          <div className="instructions-section">
            <div className="instructions">
              <div className="instruction-item">
                <span className="step-number">1</span>
                <span>{t.step1}</span>
              </div>
              <div className="instruction-item">
                <span className="step-number">2</span>
                <span>{t.step2}</span>
              </div>
              <div className="instruction-item">
                <span className="step-number">3</span>
                <span>{t.step3}</span>
              </div>
              <div className="instruction-item">
                <span className="step-number">4</span>
                <span>{t.step4}</span>
              </div>
              <div className="instruction-item">
                <span className="step-number">5</span>
                <span>{t.step5}</span>
              </div>
            </div>

            <div className="keep-connected">
              <p>{t.keepPhoneConnected}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default QRCodeWindow;
