import React from 'react';
import moment from 'moment';
import { FiCheck, FiCheckCircle, FiClock } from 'react-icons/fi';
import './MessageList.css';

function MessageList({ messages, language, chatId }) {
  const formatTime = (timestamp) => {
    if (!timestamp) return '';
    return moment(timestamp).format(language === 'he' ? 'HH:mm' : 'h:mm A');
  };

  const formatDateDivider = (timestamp) => {
    if (!timestamp) return '';
    const messageDate = moment(timestamp);
    const today = moment();
    const yesterday = moment().subtract(1, 'day');

    if (messageDate.isSame(today, 'day')) {
      return language === 'he' ? 'היום' : 'Today';
    } else if (messageDate.isSame(yesterday, 'day')) {
      return language === 'he' ? 'אתמול' : 'Yesterday';
    } else {
      return messageDate.format(language === 'he' ? 'DD/MM/YYYY' : 'MM/DD/YYYY');
    }
  };

  const getMessageIcon = (message) => {
    if (!message.fromMe) return null;

    switch (message.ack) {
      case 1:
        return <FiClock size={12} className="message-status-icon pending" />;
      case 2:
        return <FiCheck size={12} className="message-status-icon delivered" />;
      case 3:
        return <FiCheckCircle size={12} className="message-status-icon read" />;
      default:
        return <FiClock size={12} className="message-status-icon pending" />;
    }
  };

  const shouldShowDateDivider = (currentMessage, previousMessage) => {
    if (!previousMessage) return true;
    
    const currentDate = moment(currentMessage.timestamp);
    const previousDate = moment(previousMessage.timestamp);
    
    return !currentDate.isSame(previousDate, 'day');
  };

  const groupMessagesByDate = () => {
    const groups = [];
    let currentGroup = null;

    messages.forEach((message, index) => {
      const previousMessage = index > 0 ? messages[index - 1] : null;
      
      if (shouldShowDateDivider(message, previousMessage)) {
        if (currentGroup) {
          groups.push(currentGroup);
        }
        currentGroup = {
          date: message.timestamp,
          messages: [message]
        };
      } else {
        if (currentGroup) {
          currentGroup.messages.push(message);
        }
      }
    });

    if (currentGroup) {
      groups.push(currentGroup);
    }

    return groups;
  };

  const renderMessage = (message, index) => {
    const messageClass = `message ${message.fromMe ? 'sent' : 'received'} ${language === 'he' ? 'rtl' : ''}`;
    
    return (
      <div key={message.id || index} className={messageClass}>
        <div className="message-bubble">
          <div className="message-content">
            {message.type === 'image' && message.media ? (
              <div className="message-media">
                <img src={message.media} alt="Media" className="message-image" />
                {message.body && <div className="message-caption">{message.body}</div>}
              </div>
            ) : message.type === 'video' && message.media ? (
              <div className="message-media">
                <video controls className="message-video">
                  <source src={message.media} type="video/mp4" />
                  Your browser does not support the video tag.
                </video>
                {message.body && <div className="message-caption">{message.body}</div>}
              </div>
            ) : message.type === 'audio' && message.media ? (
              <div className="message-media">
                <audio controls className="message-audio">
                  <source src={message.media} type="audio/mpeg" />
                  Your browser does not support the audio tag.
                </audio>
                {message.body && <div className="message-caption">{message.body}</div>}
              </div>
            ) : message.type === 'document' && message.media ? (
              <div className="message-document">
                <div className="document-icon">📄</div>
                <div className="document-info">
                  <div className="document-name">{message.filename || 'Document'}</div>
                  <div className="document-size">{message.filesize || ''}</div>
                </div>
                <a href={message.media} download className="document-download">⬇️</a>
              </div>
            ) : (
              <div className="message-text">
                {message.body || message.text || ''}
              </div>
            )}
          </div>
          
          <div className="message-meta">
            <span className="message-time">{formatTime(message.timestamp)}</span>
            {getMessageIcon(message)}
          </div>
        </div>
      </div>
    );
  };

  if (!messages || messages.length === 0) {
    return (
      <div className={`message-list empty ${language === 'he' ? 'rtl' : ''}`}>
        <div className="empty-messages">
          <div className="empty-icon">💬</div>
          <p>{language === 'he' ? 'אין הודעות בשיחה זו' : 'No messages in this chat'}</p>
        </div>
      </div>
    );
  }

  const messageGroups = groupMessagesByDate();

  return (
    <div className={`message-list ${language === 'he' ? 'rtl' : ''}`}>
      {messageGroups.map((group, groupIndex) => (
        <div key={groupIndex} className="message-group">
          <div className="date-divider">
            <span className="date-text">{formatDateDivider(group.date)}</span>
          </div>
          {group.messages.map((message, messageIndex) => renderMessage(message, messageIndex))}
        </div>
      ))}
    </div>
  );
}

export default MessageList;
